patch:
diff --git a/Modules/_cursesmodule.c b/Modules/_cursesmodule.c
index 743b9e37fbc..8367e0047cc 100644
--- a/Modules/_cursesmodule.c
+++ b/Modules/_cursesmodule.c
@@ -3255,6 +3255,11 @@ _curses_initscr_impl(PyObject *module)
     PyCursesWindowObject *winobj;

     if (initialised) {
+        if (stdscr == NULL || getmaxy(stdscr) <= 0 || getmaxx(stdscr) <= 0) {
+            /* stdscr is invalid, reinitialize */
+            initialised = FALSE;
+            goto reinit;
+        }
         wrefresh(stdscr);
         return (PyObject *)PyCursesWindow_New(stdscr, NULL);
     }
@@ -3262,6 +3267,7 @@ _curses_initscr_impl(PyObject *module)
     win = initscr();

     if (win == NULL) {
+reinit:
         PyErr_SetString(PyCursesError, catchall_NULL);
         return NULL;
     }
