patch:
diff --git a/Modules/_cursesmodule.c b/Modules/_cursesmodule.c
index 743b9e37fbc..91c165dc024 100644
--- a/Modules/_cursesmodule.c
+++ b/Modules/_cursesmodule.c
@@ -3255,8 +3255,11 @@ _curses_initscr_impl(PyObject *module)
     PyCursesWindowObject *winobj;

     if (initialised) {
-        wrefresh(stdscr);
-        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        /* Reset initialised flag to force reinitialization.
+         * This prevents issues when endwin() was called but
+         * initialised flag wasn't properly reset. */
+        initialised = FALSE;
+        /* Fall through to reinitialize */
     }

     win = initscr();
