patch:
diff --git a/Modules/_cursesmodule.c b/Modules/_cursesmodule.c
index 743b9e37fbc..ebf647bdc8e 100644
--- a/Modules/_cursesmodule.c
+++ b/Modules/_cursesmodule.c
@@ -3253,10 +3253,16 @@ _curses_initscr_impl(PyObject *module)
 {
     WINDOW *win;
     PyCursesWindowObject *winobj;
-
+// FIXME: Crash CWE-125: Out-of-bounds Read
+ 
     if (initialised) {
-        wrefresh(stdscr);
-        return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        if (stdscr != NULL && getmaxx(stdscr) > 0 && getmaxy(stdscr) > 0) {
+            wrefresh(stdscr);
+            return (PyObject *)PyCursesWindow_New(stdscr, NULL);
+        } else {
+            PyErr_SetString(PyCursesError, "ncurses screen not properly initialized");
+            return NULL;
+        }
     }

     win = initscr();
