patch:
diff --git a/Objects/frameobject.c b/Objects/frameobject.c
index d6b4065e130..25a786d80fd 100644
--- a/Objects/frameobject.c
+++ b/Objects/frameobject.c
@@ -30,6 +30,11 @@ framelocalsproxy_getval(_PyInterpreterFrame *frame, PyCodeObject *co, int i)
     PyObject *value = PyStackRef_AsPyObjectBorrow(fast[i]);
     PyObject *cell = NULL;

+    // Defensive check: if we got a high-value pointer, it's likely invalid
+    if (value != NULL && ((uintptr_t)value) > 0x7fffffffffff) {
+        return NULL;
+    }
+
     if (value == NULL) {
         return NULL;
     }
@@ -37,16 +42,18 @@ framelocalsproxy_getval(_PyInterpreterFrame *frame, PyCodeObject *co, int i)
     if (kind == CO_FAST_FREE || kind & CO_FAST_CELL) {
         // The cell was set when the frame was created from
         // the function's closure.
-        assert(PyCell_Check(value));
-        cell = value;
+        if (PyCell_Check(value)) {
+            cell = value;
+        } else {
+            return NULL;  // Invalid cell object
+        }
     }

     if (cell != NULL) {
         value = PyCell_GET(cell);
-    }
-
-    if (value == NULL) {
-        return NULL;
+        if (value == NULL) {
+            return NULL;
+        }
     }

     return value;
@@ -135,8 +142,10 @@ framelocalsproxy_getitem(PyObject *self, PyObject *key)
     }
     if (i >= 0) {
         PyObject *value = framelocalsproxy_getval(frame->f_frame, co, i);
-        assert(value != NULL);
-        return Py_NewRef(value);
+        if (value != NULL) {
+            return Py_NewRef(value);
+        }
+        // Fall through to check extra locals
     }

     // Okay not in the fast locals, try extra locals
