patch:
diff --git a/Modules/_elementtree.c b/Modules/_elementtree.c
index b73bd844626..ebe7531daea 100644
--- a/Modules/_elementtree.c
+++ b/Modules/_elementtree.c
@@ -1641,7 +1641,14 @@ _elementtree_Element_remove_impl(ElementObject *self, PyObject *subelement)
         return NULL;
     }

-    for (i = 0; i < self->extra->length; i++) {
+    for (i = 0; self->extra && i < self->extra->length; i++) {
+        if (!self->extra) {
+            PyErr_SetString(
+                PyExc_ValueError,
+                "list.remove(x): x not in list"
+            );
+            return NULL;
+        }
         if (self->extra->children[i] == subelement)
             break;
         rc = PyObject_RichCompareBool(self->extra->children[i], subelement, Py_EQ);
@@ -1651,7 +1658,7 @@ _elementtree_Element_remove_impl(ElementObject *self, PyObject *subelement)
             return NULL;
     }

-    if (i >= self->extra->length) {
+    if (!self->extra || i >= self->extra->length) {
         /* subelement is not in children, so raise exception */
         PyErr_SetString(
             PyExc_ValueError,
@@ -1660,11 +1667,26 @@ _elementtree_Element_remove_impl(ElementObject *self, PyObject *subelement)
         return NULL;
     }

+    if (!self->extra) {
+        PyErr_SetString(
+            PyExc_ValueError,
+            "list.remove(x): x not in list"
+        );
+        return NULL;
+    }
     found = self->extra->children[i];

     self->extra->length--;
-    for (; i < self->extra->length; i++)
+    for (; self->extra && i < self->extra->length; i++) {
+        if (!self->extra) {
+            PyErr_SetString(
+                PyExc_ValueError,
+                "list.remove(x): x not in list"
+            );
+            return NULL;
+        }
         self->extra->children[i] = self->extra->children[i+1];
+    }

     Py_DECREF(found);
     Py_RETURN_NONE;
