diff --git a/Modules/_elementtree.c b/Modules/_elementtree.c
index d20bbcd21fd..f5fd57151fe 100644
--- a/Modules/_elementtree.c
+++ b/Modules/_elementtree.c
@@ -1254,6 +1254,8 @@ _elementtree_Element_find_impl(ElementObject *self, PyTypeObject *cls,
 {
     Py_ssize_t i;
     elementtreestate *st = get_elementtree_state_by_cls(cls);
+    ElementObjectExtra *extra;
+    Py_ssize_t length;

     if (checkpath(path) || namespaces != Py_None) {
         return PyObject_CallMethodObjArgs(
@@ -1261,10 +1263,16 @@ _elementtree_Element_find_impl(ElementObject *self, PyTypeObject *cls,
             );
     }

-    if (!self->extra)
+    extra = self->extra;
+    if (!extra)
         Py_RETURN_NONE;

-    for (i = 0; i < self->extra->length; i++) {
+    /* Store length to avoid accessing freed memory */
+    length = extra->length;
+    
+    for (i = 0; i < length; i++) {
+        /* Re-check that extra is still valid before each access */
+        if (!self->extra || i >= self->extra->length) break;
         PyObject* item = self->extra->children[i];
         int rc;
         assert(Element_Check(st, item));
