--- a/Modules/_elementtree.c
+++ b/Modules/_elementtree.c
@@ -1255,19 +1255,25 @@ _elementtree_Element_find_impl(ElementObject *self, PyTypeObject *cls,
     Py_ssize_t i;
     elementtreestate *st = get_elementtree_state_by_cls(cls);

+    if (self == NULL || self->extra == NULL) {
+        Py_RETURN_NONE;
+    }
+
     if (checkpath(path) || namespaces != Py_None) {
         return PyObject_CallMethodObjArgs(
             st->elementpath_obj, st->str_find, self, path, namespaces, NULL
             );
     }

-    if (!self->extra)
-        Py_RETURN_NONE;

-    for (i = 0; i < self->extra->length; i++) {
+    /* Defensive: re-check self->extra and children inside loop in case of race/corruption. */
+    for (i = 0; self->extra && self->extra->children && i < self->extra->length; i++) {
         PyObject* item = self->extra->children[i];
         int rc;
-        assert(Element_Check(st, item));
+        if (item == NULL)
+            continue;
+        if (!Element_Check(st, item))
+            continue;
         Py_INCREF(item);
         rc = PyObject_RichCompareBool(((ElementObject*)item)->tag, path, Py_EQ);
