patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 18dc2859357..e29fce639c6 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -294,7 +294,15 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
     if (ctx == NULL) {
         PyObject *stack[] = {loop, func, arg};
         size_t nargsf = 3 | PY_VECTORCALL_ARGUMENTS_OFFSET;
+        /* Increment reference counts to prevent use-after-free during call */
+        Py_INCREF(loop);
+        Py_INCREF(func);
+        Py_XINCREF(arg);
         handle = PyObject_VectorcallMethod(&_Py_ID(call_soon), stack, nargsf, NULL);
+        /* Decrement reference counts after call */
+        Py_DECREF(loop);
+        Py_DECREF(func);
+        Py_XDECREF(arg);
     }
     else {
         /* All refs in 'stack' are borrowed. */
@@ -308,8 +316,18 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
         }
         stack[nargs] = (PyObject *)ctx;
         size_t nargsf = nargs | PY_VECTORCALL_ARGUMENTS_OFFSET;
+        /* Increment reference counts to prevent use-after-free during call */
+        Py_INCREF(loop);
+        Py_INCREF(func);
+        Py_XINCREF(arg);
+        Py_INCREF(ctx);
         handle = PyObject_VectorcallMethod(&_Py_ID(call_soon), stack, nargsf,
                                            state->context_kwname);
+        /* Decrement reference counts after call */
+        Py_DECREF(loop);
+        Py_DECREF(func);
+        Py_XDECREF(arg);
+        Py_DECREF(ctx);
     }

     if (handle == NULL) {
