diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 18dc2859357..3eba678e72a 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -292,9 +292,16 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
     PyObject *handle;

     if (ctx == NULL) {
+        /* Ensure we hold references to all objects in the stack */
+        Py_INCREF(loop);
+        Py_INCREF(func);
+        Py_XINCREF(arg);
         PyObject *stack[] = {loop, func, arg};
         size_t nargsf = 3 | PY_VECTORCALL_ARGUMENTS_OFFSET;
         handle = PyObject_VectorcallMethod(&_Py_ID(call_soon), stack, nargsf, NULL);
+        Py_DECREF(loop);
+        Py_DECREF(func);
+        Py_XDECREF(arg);
     }
     else {
         /* All refs in 'stack' are borrowed. */
@@ -302,6 +309,11 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
         size_t nargs = 2;
         stack[0] = loop;
         stack[1] = func;
+        /* Ensure we hold references to all objects in the stack */
+        Py_INCREF(loop);
+        Py_INCREF(func);
+        Py_XINCREF(arg);
+        Py_INCREF(ctx);
         if (arg != NULL) {
             stack[2] = arg;
             nargs++;
@@ -310,6 +322,10 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
         size_t nargsf = nargs | PY_VECTORCALL_ARGUMENTS_OFFSET;
         handle = PyObject_VectorcallMethod(&_Py_ID(call_soon), stack, nargsf,
                                            state->context_kwname);
+        Py_DECREF(loop);
+        Py_DECREF(func);
+        Py_XDECREF(arg);
+        Py_DECREF(ctx);
     }

     if (handle == NULL) {
