patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 18dc2859357..af9def57c7a 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -290,6 +290,11 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
           PyObject *ctx)
 {
     PyObject *handle;
+    /* Hold references to prevent deallocation during call */
+    Py_INCREF(loop);
+    Py_INCREF(func);
+    Py_XINCREF(arg);
+    Py_XINCREF(ctx);

     if (ctx == NULL) {
         PyObject *stack[] = {loop, func, arg};
@@ -312,6 +317,12 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
                                            state->context_kwname);
     }

+    /* Release the references we held */
+    Py_DECREF(loop);
+    Py_DECREF(func);
+    Py_XDECREF(arg);
+    Py_XDECREF(ctx);
+
     if (handle == NULL) {
         return -1;
     }
