diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 18dc2859357..eb67b1434a7 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -292,9 +292,15 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
     PyObject *handle;

     if (ctx == NULL) {
-        PyObject *stack[] = {loop, func, arg};
+        PyObject *stack[3];
+        stack[0] = Py_NewRef(loop);
+        stack[1] = Py_NewRef(func);
+        stack[2] = Py_NewRef(arg);
         size_t nargsf = 3 | PY_VECTORCALL_ARGUMENTS_OFFSET;
         handle = PyObject_VectorcallMethod(&_Py_ID(call_soon), stack, nargsf, NULL);
+        Py_DECREF(stack[0]);
+        Py_DECREF(stack[1]);
+        Py_DECREF(stack[2]);
     }
     else {
         /* All refs in 'stack' are borrowed. */
@@ -302,14 +308,24 @@ call_soon(asyncio_state *state, PyObject *loop, PyObject *func, PyObject *arg,
         size_t nargs = 2;
         stack[0] = loop;
         stack[1] = func;
+        Py_INCREF(stack[0]);
+        Py_INCREF(stack[1]);
         if (arg != NULL) {
             stack[2] = arg;
+            Py_INCREF(stack[2]);
             nargs++;
         }
         stack[nargs] = (PyObject *)ctx;
+        Py_INCREF(stack[nargs]);
         size_t nargsf = nargs | PY_VECTORCALL_ARGUMENTS_OFFSET;
         handle = PyObject_VectorcallMethod(&_Py_ID(call_soon), stack, nargsf,
                                            state->context_kwname);
+        Py_DECREF(stack[0]);
+        Py_DECREF(stack[1]);
+        if (arg != NULL) {
+            Py_DECREF(stack[2]);
+        }
+        Py_DECREF(stack[nargs]);
     }

     if (handle == NULL) {
