patch:
diff --git a/Modules/_asynciomodule.c b/Modules/_asynciomodule.c
index 7483e9c0f43..29425cbadeb 100644
--- a/Modules/_asynciomodule.c
+++ b/Modules/_asynciomodule.c
@@ -2948,6 +2948,16 @@ task_step_handle_result_impl(asyncio_state *state, TaskObj *task, PyObject *resu

         fut->fut_blocking = 0;

+        /* Handle cancellation early to avoid use-after-free */
+        if (task->task_must_cancel) {
+            Py_INCREF(result);  /* Protect during cancel call */
+            PyObject *r = PyObject_CallMethodOneArg(result, &_Py_ID(cancel),
+                                                   task->task_cancel_msg);
+            Py_DECREF(result);
+            Py_XDECREF(r);  /* r might be NULL if cancel failed */
+            task->task_must_cancel = 0;
+        }
+
         /* result.add_done_callback(task._wakeup) */
         wrapper = PyCFunction_New(&TaskWakeupDef, (PyObject *)task);
         if (wrapper == NULL) {
@@ -2964,24 +2974,6 @@ task_step_handle_result_impl(asyncio_state *state, TaskObj *task, PyObject *resu
         /* task._fut_waiter = result */
         task->task_fut_waiter = result;  /* no incref is necessary */

-        if (task->task_must_cancel) {
-            PyObject *r;
-            int is_true;
-            r = PyObject_CallMethodOneArg(result, &_Py_ID(cancel),
-                                             task->task_cancel_msg);
-            if (r == NULL) {
-                return NULL;
-            }
-            is_true = PyObject_IsTrue(r);
-            Py_DECREF(r);
-            if (is_true < 0) {
-                return NULL;
-            }
-            else if (is_true) {
-                task->task_must_cancel = 0;
-            }
-        }
-
         Py_RETURN_NONE;
     }

@@ -3030,6 +3022,16 @@ task_step_handle_result_impl(asyncio_state *state, TaskObj *task, PyObject *resu
             goto fail;
         }

+        /* Handle cancellation early to avoid use-after-free */
+        if (task->task_must_cancel) {
+            Py_INCREF(result);  /* Protect during cancel call */
+            PyObject *r = PyObject_CallMethodOneArg(result, &_Py_ID(cancel),
+                                                   task->task_cancel_msg);
+            Py_DECREF(result);
+            Py_XDECREF(r);  /* r might be NULL if cancel failed */
+            task->task_must_cancel = 0;
+        }
+
         wrapper = PyCFunction_New(&TaskWakeupDef, (PyObject *)task);
         if (wrapper == NULL) {
             goto fail;
@@ -3057,24 +3059,6 @@ task_step_handle_result_impl(asyncio_state *state, TaskObj *task, PyObject *resu
         /* task._fut_waiter = result */
         task->task_fut_waiter = result;  /* no incref is necessary */

-        if (task->task_must_cancel) {
-            PyObject *r;
-            int is_true;
-            r = PyObject_CallMethodOneArg(result, &_Py_ID(cancel),
-                                             task->task_cancel_msg);
-            if (r == NULL) {
-                return NULL;
-            }
-            is_true = PyObject_IsTrue(r);
-            Py_DECREF(r);
-            if (is_true < 0) {
-                return NULL;
-            }
-            else if (is_true) {
-                task->task_must_cancel = 0;
-            }
-        }
-
         Py_RETURN_NONE;
     }
