patch:
diff --git a/Modules/arraymodule.c b/Modules/arraymodule.c
index b80c964f20d..6f1a3ab7f7b 100644
--- a/Modules/arraymodule.c
+++ b/Modules/arraymodule.c
@@ -3090,7 +3090,12 @@ array_arrayiterator___setstate__(arrayiterobject *self, PyObject *state)
     Py_ssize_t index = PyLong_AsSsize_t(state);
     if (index == -1 && PyErr_Occurred())
         return NULL;
-    if (index < 0)
+    // Fix: Ensure self->ao is initialized before accessing
+    if (self->ao == NULL) {
+        PyErr_SetString(PyExc_RuntimeError, "array iterator object is not properly initialized");
+        return NULL;
+    }
+     if (index < 0)
         index = 0;
     else if (index > Py_SIZE(self->ao))
         index = Py_SIZE(self->ao); /* iterator exhausted */
