patch:
diff --git a/Include/object.h b/Include/object.h
index 8cc83abb857..c6462c5354f 100644
--- a/Include/object.h
+++ b/Include/object.h
@@ -257,7 +257,6 @@ _Py_IsOwnedByCurrentThread(PyObject *ob)
 #endif
 }
 #endif
-
 // Py_TYPE() implementation for the stable ABI
 PyAPI_FUNC(PyTypeObject*) Py_TYPE(PyObject *ob);

@@ -265,15 +264,20 @@ PyAPI_FUNC(PyTypeObject*) Py_TYPE(PyObject *ob);
     // Stable ABI implements Py_TYPE() as a function call
     // on limited C API version 3.14 and newer.
 #else
-    static inline PyTypeObject* _Py_TYPE(PyObject *ob)
-    {
-        return ob->ob_type;
+#include "pyerrors.h"
+static inline PyTypeObject* _Py_TYPE(PyObject *ob)
+{
+    if (ob == NULL) {
+        Py_FatalError("NULL pointer passed to _Py_TYPE");
+        return NULL;
     }
-    #if !defined(Py_LIMITED_API) || Py_LIMITED_API+0 < 0x030b0000
-    #   define Py_TYPE(ob) _Py_TYPE(_PyObject_CAST(ob))
-    #else
-    #   define Py_TYPE(ob) _Py_TYPE(ob)
-    #endif
+    return ob->ob_type;
+}
+#if !defined(Py_LIMITED_API) || Py_LIMITED_API+0 < 0x030b0000
+#   define Py_TYPE(ob) _Py_TYPE(_PyObject_CAST(ob))
+#else
+#   define Py_TYPE(ob) _Py_TYPE(ob)
+#endif
 #endif

 PyAPI_DATA(PyTypeObject) PyLong_Type;
