diff --git a/Objects/typeobject.c b/Objects/typeobject.c
index a7ab69fef4c..136377f1de0 100644
--- a/Objects/typeobject.c
+++ b/Objects/typeobject.c
@@ -5752,13 +5752,9 @@ _PyType_LookupStackRefAndVersion(PyTypeObject *type, PyObject *name, _PyStackRef
         }
     }
 #else
-    if (entry->version == type->tp_version_tag && entry->name == name) {
-        assert(type->tp_version_tag);
-        OBJECT_STAT_INC_COND(type_cache_hits, !is_dunder_name(name));
-        OBJECT_STAT_INC_COND(type_cache_dunder_hits, is_dunder_name(name));
-        *out = entry->value ? PyStackRef_FromPyObjectNew(entry->value) : PyStackRef_NULL;
-        return entry->version;
-    }
+    // Disable cache for non-GIL-disabled builds to prevent use-after-free
+    // The cache stores borrowed references which can become dangling pointers
+    // The GIL-disabled code path has proper atomic handling for this case
 #endif
     OBJECT_STAT_INC_COND(type_cache_misses, !is_dunder_name(name));
     OBJECT_STAT_INC_COND(type_cache_dunder_misses, is_dunder_name(name));
diff --git a/Python/sysmodule.c b/Python/sysmodule.c
index dd048205757..2b2c598e35e 100644
--- a/Python/sysmodule.c
+++ b/Python/sysmodule.c
@@ -2482,7 +2482,7 @@ static PyObject *
 sys_remote_exec_impl(PyObject *module, int pid, PyObject *script)
 /*[clinic end generated code: output=7d94c56afe4a52c0 input=39908ca2c5fe1eb0]*/
 {
-    PyObject *path;
+    PyObject *path = NULL;
     const char *debugger_script_path;

     if (PyUnicode_FSConverter(script, &path) < 0) {
@@ -2539,7 +2539,7 @@ sys_remote_exec_impl(PyObject *module, int pid, PyObject *script)
     Py_RETURN_NONE;

 error:
-    Py_DECREF(path);
+    Py_XDECREF(path);
     return NULL;
 }
