patch:
diff --git a/Python/import.c b/Python/import.c
index 9dec0f488a3..fe7d9d277d7 100644
--- a/Python/import.c
+++ b/Python/import.c
@@ -3852,17 +3852,19 @@ PyImport_ImportModuleLevelObject(PyObject *name, PyObject *globals,
                 if (to_return == NULL) {
                     goto error;
                 }
-
+// FIXME: Crash CWE-416: Use After Free
+ 
                 final_mod = import_get_module(tstate, to_return);
-                Py_DECREF(to_return);
                 if (final_mod == NULL) {
                     if (!_PyErr_Occurred(tstate)) {
                         _PyErr_Format(tstate, PyExc_KeyError,
                                       "%R not in sys.modules as expected",
                                       to_return);
                     }
+                    Py_DECREF(to_return);
                     goto error;
                 }
+                Py_DECREF(to_return);
             }
         }
         else {
