patch:
diff --git a/quickjs.c b/quickjs.c
index 46e124a..9b5cfb9 100644
--- a/quickjs.c
+++ b/quickjs.c
@@ -50325,11 +50325,20 @@ static JSValue js_typed_array_slice(JSContext *ctx, JSValue this_val,

         p1 = get_typed_array(ctx, arr, 0);
         if (p1 != NULL && p->class_id == p1->class_id &&
-            typed_array_get_length(ctx, p1) >= count &&
+// FIXME: Crash CWE-119: Buffer Overflow
+             typed_array_get_length(ctx, p1) >= count &&
             typed_array_get_length(ctx, p) >= start + count) {
-            memcpy(p1->u.array.u.uint8_ptr,
-                   p->u.array.u.uint8_ptr + (start << shift),
-                   count << shift);
+            // Check for overlapping memory regions before using memcpy
+            uint8_t *src = p->u.array.u.uint8_ptr + (start << shift);
+            uint8_t *dst = p1->u.array.u.uint8_ptr;
+            size_t copy_size = count << shift;
+            
+            if (src + copy_size > dst && src < dst + copy_size) {
+                // Use memmove for overlapping regions
+                memmove(dst, src, copy_size);
+            } else {
+                memcpy(dst, src, copy_size);
+            }
         } else {
             for (n = 0; n < count; n++) {
                 val = JS_GetPropertyValue(ctx, this_val, js_int32(start + n));
