diff --git a/quickjs.c b/quickjs.c
index 46e124a..c0c18d6 100644
--- a/quickjs.c
+++ b/quickjs.c
@@ -50325,11 +50325,27 @@ static JSValue js_typed_array_slice(JSContext *ctx, JSValue this_val,

         p1 = get_typed_array(ctx, arr, 0);
         if (p1 != NULL && p->class_id == p1->class_id &&
-            typed_array_get_length(ctx, p1) >= count &&
+// FIXME: Crash CWE-119: Buffer Overflow
+             typed_array_get_length(ctx, p1) >= count &&
             typed_array_get_length(ctx, p) >= start + count) {
-            memcpy(p1->u.array.u.uint8_ptr,
-                   p->u.array.u.uint8_ptr + (start << shift),
-                   count << shift);
+            uint8_t *src = p->u.array.u.uint8_ptr + (start << shift);
+            uint8_t *dest = p1->u.array.u.uint8_ptr;
+            size_t size = count << shift;
+            
+            // Check for memory overlap to prevent buffer overflow
+            if (dest < src + size && src < dest + size) {
+                // Use element-by-element copy for overlapping regions
+                for (n = 0; n < count; n++) {
+                    val = JS_GetPropertyValue(ctx, this_val, js_int32(start + n));
+                    if (JS_IsException(val))
+                        goto exception;
+                    if (JS_SetPropertyValue(ctx, arr, js_int32(n), val,
+                                            JS_PROP_THROW) < 0)
+                        goto exception;
+                }
+            } else {
+                memcpy(dest, src, size);
+            }
         } else {
             for (n = 0; n < count; n++) {
                 val = JS_GetPropertyValue(ctx, this_val, js_int32(start + n));
