patch:
diff --git a/libtiff/tif_dir.c b/libtiff/tif_dir.c
index 459f9696..6bff23cb 100644
--- a/libtiff/tif_dir.c
+++ b/libtiff/tif_dir.c
@@ -1148,6 +1148,9 @@ int TIFFSetField(TIFF *tif, uint32_t tag, ...)
     va_list ap;
     int status;

+    if (tif == NULL) {
+        return 0;
+    }
     va_start(ap, tag);
     status = TIFFVSetField(tif, tag, ap);
     va_end(ap);
@@ -1204,6 +1207,9 @@ int TIFFUnsetField(TIFF *tif, uint32_t tag)
  */
 int TIFFVSetField(TIFF *tif, uint32_t tag, va_list ap)
 {
+    if (tif == NULL || tif->tif_tagmethods.vsetfield == NULL) {
+        return 0;
+    }
     return OkToChangeTag(tif, tag)
                ? (*tif->tif_tagmethods.vsetfield)(tif, tag, ap)
                : 0;
