diff --git a/src/objects/js-date-time-format.cc b/src/objects/js-date-time-format.cc
index c51ac6da12e..0f50d723b41 100644
--- a/src/objects/js-date-time-format.cc
+++ b/src/objects/js-date-time-format.cc
@@ -1689,6 +1689,10 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
   uint16_t m0 = flat.Get(p);
   if (m0 == ':') {
     // Ignore ':'
+    if (p + 1 >= len) {
+      // Error: not enough characters after ':'
+      return std::nullopt;
+    }
     p++;
     m0 = flat.Get(p);
   }
