diff --git a/src/objects/js-date-time-format.cc b/src/objects/js-date-time-format.cc
index c51ac6da12e..6cb42986218 100644
--- a/src/objects/js-date-time-format.cc
+++ b/src/objects/js-date-time-format.cc
@@ -1658,6 +1658,8 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
     return std::nullopt;
   }
   std::string tz("GMT");
+  // Bounds check for flat.Get(0), flat.Get(1), flat.Get(2)
+  if (len < 3) return std::nullopt;
   switch (flat.Get(0)) {
     case 0x2212:
     case '-':
@@ -1671,6 +1673,7 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
       return std::nullopt;
   }
   // 00 - 23
+  if (len < 3) return std::nullopt;
   uint16_t h0 = flat.Get(1);
   uint16_t h1 = flat.Get(2);

@@ -1686,16 +1689,21 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
     return tz;
   }
   int32_t p = 3;
+  // Bounds check for flat.Get(p)
+  if (len <= p) return std::nullopt;
   uint16_t m0 = flat.Get(p);
   if (m0 == ':') {
     // Ignore ':'
     p++;
+    if (len <= p) return std::nullopt;
     m0 = flat.Get(p);
   }
   if (len - p != 2) {
     // Error
     return std::nullopt;
   }
+  // Bounds check for flat.Get(p + 1)
+  if (len <= p + 1) return std::nullopt;
   uint16_t m1 = flat.Get(p + 1);
   if (m0 >= '0' && m0 <= '5' && m1 >= '0' && m1 <= '9') {
     tz += m0;
