patch:
diff --git a/src/objects/js-date-time-format.cc b/src/objects/js-date-time-format.cc
index c51ac6da12e..eb42580d155 100644
--- a/src/objects/js-date-time-format.cc
+++ b/src/objects/js-date-time-format.cc
@@ -1658,6 +1658,9 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
     return std::nullopt;
   }
   std::string tz("GMT");
+  // Bounds already checked for len >= 3 above
+  if (len < 3)
+    return std::nullopt;
   switch (flat.Get(0)) {
     case 0x2212:
     case '-':
@@ -1671,9 +1674,10 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
       return std::nullopt;
   }
   // 00 - 23
+  if (len < 3)
+    return std::nullopt;
   uint16_t h0 = flat.Get(1);
   uint16_t h1 = flat.Get(2);
-
   if ((h0 >= '0' && h0 <= '1' && h1 >= '0' && h1 <= '9') ||
       (h0 == '2' && h1 >= '0' && h1 <= '3')) {
     tz += h0;
@@ -1686,16 +1690,21 @@ std::optional<std::string> GetOffsetTimeZone(Isolate* isolate,
     return tz;
   }
   int32_t p = 3;
+  if (len <= p)
+    return std::nullopt;
   uint16_t m0 = flat.Get(p);
   if (m0 == ':') {
-    // Ignore ':'
     p++;
+    if (len <= p)
+      return std::nullopt;
     m0 = flat.Get(p);
   }
   if (len - p != 2) {
     // Error
     return std::nullopt;
   }
+  if (len <= p + 1)
+    return std::nullopt;
   uint16_t m1 = flat.Get(p + 1);
   if (m0 >= '0' && m0 <= '5' && m1 >= '0' && m1 <= '9') {
     tz += m0;
