diff --git a/src/compiler/turboshaft/typer.h b/src/compiler/turboshaft/typer.h
index f8c2ba1e9cf..9ae05326182 100644
--- a/src/compiler/turboshaft/typer.h
+++ b/src/compiler/turboshaft/typer.h
@@ -1159,7 +1159,10 @@ class Typer {

   static Type TypeForRepresentation(
       base::Vector<const RegisterRepresentation> reps, Zone* zone) {
-    DCHECK_LT(0, reps.size());
+    if (reps.size() == 0) {
+      // Defensive: no representations, return the most generic type.
+      return Type::Any();
+    }
     if (reps.size() == 1) return TypeForRepresentation(reps[0]);
     base::SmallVector<Type, 4> tuple_types;
     for (auto rep : reps) tuple_types.push_back(TypeForRepresentation(rep));
