patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..c3e8a82e3 100644
--- a/src/help.c
+++ b/src/help.c
@@ -423,7 +423,12 @@ find_help_tags(
 							   && arg[2] != NUL)))
 	{
 	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+	    // Ensure we don't overflow the buffer
+	    if (STRLEN(arg + 1) < IOSIZE - 4)
+		STRCPY(d + 3, arg + 1);
+	    else {
+		vim_strncpy(d + 3, arg + 1, IOSIZE - 4 - 1);
+	    }
 	    // Check for "/\\_$", should be "/\\_\$"
 	    if (d[3] == '_' && d[4] == '$')
 		STRCPY(d + 4, "\\$");
@@ -450,7 +455,7 @@ find_help_tags(
 	    // completion.
 	    // Insert a backslash before '~', '$' and '.' to avoid their
 	    // special meaning.
-	    if (d - IObuff > IOSIZE - 10)	// getting too long!?
+	    if (d - IObuff > IOSIZE - 20)	// getting too long!?
 		break;
 	    switch (*s)
 	    {
