patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..f09f83e30 100644
--- a/src/help.c
+++ b/src/help.c
@@ -422,11 +422,12 @@ find_help_tags(
 		    || (vim_strchr((char_u *)"%_z@", arg[1]) != NULL
 							   && arg[2] != NUL)))
 	{
-	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+        STRCPY(d, "/\\");
+        /* Ensure we do not overflow IObuff: copy at most IOSIZE - 3 - 1 bytes */
+        vim_strncpy(d + 3, arg + 1, IOSIZE - 3 - 1);
 	    // Check for "/\\_$", should be "/\\_\$"
-	    if (d[3] == '_' && d[4] == '$')
-		STRCPY(d + 4, "\\$");
+        if (d[3] == '_' && d[4] == '$')
+            STRCPY(d + 4, "\\$");
 	}
 	else
 	{
