patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..1190dd4b9 100644
--- a/src/help.c
+++ b/src/help.c
@@ -423,10 +423,14 @@ find_help_tags(
 							   && arg[2] != NUL)))
 	{
 	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+    /* Ensure we do not overflow IObuff (size IOSIZE).
+     * We already wrote 3 bytes, so only copy up to IOSIZE - 4 bytes from arg+1.
+     */
+    vim_strncpy(d + 3, arg + 1, IOSIZE - 4);
 	    // Check for "/\\_$", should be "/\\_\$"
-	    if (d[3] == '_' && d[4] == '$')
-		STRCPY(d + 4, "\\$");
+    if (d[3] == '_' && d[4] == '$')
+        /* Again, ensure we do not overflow IObuff. */
+        vim_strncpy(d + 4, "\\$", IOSIZE - 5);
 	}
 	else
 	{
