patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..37801886d 100644
--- a/src/help.c
+++ b/src/help.c
@@ -422,11 +422,25 @@ find_help_tags(
 		    || (vim_strchr((char_u *)"%_z@", arg[1]) != NULL
 							   && arg[2] != NUL)))
 	{
-	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
-	    // Check for "/\\_$", should be "/\\_\$"
-	    if (d[3] == '_' && d[4] == '$')
-		STRCPY(d + 4, "\\$");
+        /* Copy "/\\" to d, then arg+1, but do not overflow IObuff */
+        {
+            size_t d_off = 0;
+            vim_strncpy(d, (char_u *)"/\\", IOSIZE - 1);
+            d_off = STRLEN(d);
+            /* Copy arg+1, but only up to IOSIZE-1-d_off chars */
+            vim_strncpy(d + d_off, arg + 1, IOSIZE - 1 - d_off);
+            d_off = STRLEN(d);
+            // Check for "/\\_$", should be "/\\_\$"
+            if (d_off >= 5 && d[3] == '_' && d[4] == '$') {
+                /* Only copy if space remains */
+                if (d_off < IOSIZE - 2) {
+                    vim_strncpy(d + 4, "\\$", IOSIZE - 1 - 4);
+                } else {
+                    d[IOSIZE-1] = NUL;
+                }
+            }
+            d[IOSIZE-1] = NUL;
+        }
 	}
 	else
 	{
