patch:
diff --git a/src/help.c b/src/help.c
index 28d914c82..8b3db12c2 100644
--- a/src/help.c
+++ b/src/help.c
@@ -413,7 +413,7 @@ find_help_tags(
     if (d[0] == NUL)	// no match in table
     {
 	// Replace "\S" with "/\\S", etc.  Otherwise every tag is matched.
-	// Also replace "\%^" and "\%(", they match every tag too.
+	// Also replace "\%^" and "\%((", they match every tag too.
 	// Also "\zs", "\z1", etc.
 	// Also "\@<", "\@=", "\@<=", etc.
 	// And also "\_$" and "\_^".
@@ -422,11 +422,11 @@ find_help_tags(
 		    || (vim_strchr((char_u *)"%_z@", arg[1]) != NULL
 							   && arg[2] != NUL)))
 	{
-	    STRCPY(d, "/\\\\");
-	    STRCPY(d + 3, arg + 1);
+	    vim_strncpy(d, (char_u *)"/\\\\", 1024);
+	    vim_strncpy(d + 3, arg + 1, 1024 - 3);
 	    // Check for "/\\_$", should be "/\\_\$"
 	    if (d[3] == '_' && d[4] == '$')
-		STRCPY(d + 4, "\\$");
+		vim_strncpy(d + 4, (char_u *)"\\$", 1024 - 4);
 	}
 	else
 	{
