patch:
diff --git a/src/misc1.c b/src/misc1.c
index 90cf91474..2948b5abd 100644
--- a/src/misc1.c
+++ b/src/misc1.c
@@ -547,6 +547,9 @@ gchar_pos(pos_T *pos)
     // When searching columns is sometimes put at the end of a line.
     if (pos->col == MAXCOL)
 	return NUL;
+    // Check if column is beyond line length
+    if (pos->col >= ml_get_len(pos->lnum))
+	return NUL;
     ptr = ml_get_pos(pos);
     if (has_mbyte)
 	return (*mb_ptr2char)(ptr);
diff --git a/src/register.c b/src/register.c
index 3ca425b45..86908de0a 100644
--- a/src/register.c
+++ b/src/register.c
@@ -1268,8 +1268,15 @@ op_yank(oparg_T *oap, int deleting, int mess)

 		    charwise_block_prep(oap->start, oap->end, &bd, lnum, oap->inclusive);

-		    // make sure bd.textlen is not longer than the text
-		    tmp = (int)STRLEN(bd.textstart);
+		    // make sure bd.textlen is not longer than the available text
+		    // Calculate the maximum safe length from bd.textstart to end of line
+		    {
+			colnr_T line_len = ml_get_len(lnum);
+			colnr_T max_len = (bd.textcol <= line_len) ? line_len - bd.textcol : 0;
+			char_u *end_ptr = bd.textstart;
+			while (max_len > 0 && *end_ptr != NUL) { end_ptr++; max_len--; }
+			tmp = (int)(end_ptr - bd.textstart);
+		    }
 		    if (tmp < bd.textlen)
 			bd.textlen = tmp;
