patch:
diff --git a/src/misc1.c b/src/misc1.c
index 90cf91474..c67f80cbf 100644
--- a/src/misc1.c
+++ b/src/misc1.c
@@ -547,6 +547,9 @@ gchar_pos(pos_T *pos)
     // When searching columns is sometimes put at the end of a line.
     if (pos->col == MAXCOL)
 	return NUL;
+    // Check if column is within line bounds
+    if (pos->col >= ml_get_len(pos->lnum))
+	return NUL;
     ptr = ml_get_pos(pos);
     if (has_mbyte)
 	return (*mb_ptr2char)(ptr);
diff --git a/src/register.c b/src/register.c
index 3ca425b45..f49b7b4fb 100644
--- a/src/register.c
+++ b/src/register.c
@@ -1269,7 +1269,8 @@ op_yank(oparg_T *oap, int deleting, int mess)
 		    charwise_block_prep(oap->start, oap->end, &bd, lnum, oap->inclusive);

 		    // make sure bd.textlen is not longer than the text
-		    tmp = (int)STRLEN(bd.textstart);
+	    tmp = (int)ml_get_len(lnum) - bd.textcol;
+	    if (tmp < 0) tmp = 0;
 		    if (tmp < bd.textlen)
 			bd.textlen = tmp;
