index 6cf2b9d2f..fce9990df 100644
--- a/src/normal.c
+++ b/src/normal.c
@@ -7541,7 +7541,6 @@ nv_drop(cmdarg_T *cap UNUSED)
     do_put('~', NULL, BACKWARD, 1L, PUT_CURSEND);
 }
 #endif
-
 /*
  * Trigger CursorHold event.
  * When waiting for a character for 'updatetime' K_CURSORHOLD is put in the
@@ -7550,7 +7549,12 @@ nv_drop(cmdarg_T *cap UNUSED)
     static void
 nv_cursorhold(cmdarg_T *cap)
 {
+    // Ensure curbuf is valid before autocmd processing
+    if (curbuf == NULL)
+        return;
+    
     apply_autocmds(EVENT_CURSORHOLD, NULL, NULL, FALSE, curbuf);
     did_cursorhold = TRUE;
-    cap->retval |= CA_COMMAND_BUSY;	// don't call edit() now
-}
+    if (cap != NULL)
+        cap->retval |= CA_COMMAND_BUSY;	// don't call edit() now
+}
