patch:
diff --git a/src/ops.c b/src/ops.c
index a75efab59..81e6ad219 100644
--- a/src/ops.c
+++ b/src/ops.c
@@ -4484,35 +4484,36 @@ do_pending_operator(cmdarg_T *cap, int old_col, int gui_yank)
 		CancelRedo();
 	    }
 	    else
-	    {
-		// This is a new edit command, not a restart.  Need to
-		// remember it to make 'insertmode' work with mappings for
-		// Visual mode.  But do this only once.
-		restart_edit_save = restart_edit;
-		restart_edit = 0;
+    {
+	// This is a new edit command, not a restart.  Need to
+	// remember it to make 'insertmode' work with mappings for
+	// Visual mode.  But do this only once.
+	restart_edit_save = restart_edit;
+	restart_edit = 0;
 #ifdef FEAT_LINEBREAK
-		// Restore linebreak, so that when the user edits it looks as
-		// before.
-		restore_lbr(lbr_saved);
+	// Restore linebreak, so that when the user edits it looks as
+	// before.
+	restore_lbr(lbr_saved);
 #endif
-		// trigger TextChangedI
-		curbuf->b_last_changedtick_i = CHANGEDTICK(curbuf);
+	// FIXED CWE-125: Ensure buffer is null-terminated before triggering TextChangedI
+	// trigger TextChangedI
+	curbuf->b_last_changedtick_i = CHANGEDTICK(curbuf); /* Assumes CHANGEDTICK is safe, if not, buffer must be validated before use */

-		op_insert(oap, cap->count1);
+	op_insert(oap, cap->count1);
 #ifdef FEAT_LINEBREAK
-		// Reset linebreak, so that formatting works correctly.
-		(void)reset_lbr();
+	// Reset linebreak, so that formatting works correctly.
+	(void)reset_lbr();
 #endif

-		// TODO: when inserting in several lines, should format all
-		// the lines.
-		auto_format(FALSE, TRUE);
+	// TODO: when inserting in several lines, should format all
+	// the lines.
+	auto_format(FALSE, TRUE);

-		if (restart_edit == 0)
-		    restart_edit = restart_edit_save;
-		else
-		    cap->retval |= CA_COMMAND_BUSY;
-	    }
+	if (restart_edit == 0)
+	    restart_edit = restart_edit_save;
+	else
+	    cap->retval |= CA_COMMAND_BUSY;
+    }
 	    break;

 	case OP_REPLACE:
