patch:
diff --git a/src/ops.c b/src/ops.c
index a75efab59..11dc3aea6 100644
--- a/src/ops.c
+++ b/src/ops.c
@@ -1015,7 +1015,7 @@ op_delete(oparg_T *oap)
     {
 	if (virtual_op)
 	{
-	    int		endcol = 0;
+	    int	endcol = 0;

 	    // For virtualedit: break the tabs that are partly included.
 	    if (gchar_pos(&oap->start) == '\t')
@@ -1024,7 +1024,12 @@ op_delete(oparg_T *oap)
 		    return FAIL;
 		if (oap->line_count == 1)
 		    endcol = getviscol2(oap->end.col, oap->end.coladd);
-		coladvance_force(getviscol2(oap->start.col, oap->start.coladd));
+// FIXME: Crash CWE-125: Out-of-bounds Read
+		// Guard: ensure oap->start.col is valid before accessing
+		if (oap->start.col < (colnr_T)STRLEN(ml_get(oap->start.lnum)))
+		    coladvance_force(getviscol2(oap->start.col, oap->start.coladd));
+		else
+		    coladvance_force(0); // safe fallback
 		oap->start = curwin->w_cursor;
 		if (oap->line_count == 1)
 		{
@@ -1041,10 +1046,13 @@ op_delete(oparg_T *oap)
 	    {
 		// save last line for undo
 		if (u_save((linenr_T)(oap->end.lnum - 1),
-				       (linenr_T)(oap->end.lnum + 1)) == FAIL)
+			       (linenr_T)(oap->end.lnum + 1)) == FAIL)
 		    return FAIL;
 		curwin->w_cursor = oap->end;
-		coladvance_force(getviscol2(oap->end.col, oap->end.coladd));
+		if (oap->end.col < (colnr_T)STRLEN(ml_get(oap->end.lnum)))
+		    coladvance_force(getviscol2(oap->end.col, oap->end.coladd));
+		else
+		    coladvance_force(0);
 		oap->end = curwin->w_cursor;
 		curwin->w_cursor = oap->start;
 	    }
@@ -1052,6 +1060,7 @@ op_delete(oparg_T *oap)
 		mb_adjust_opend(oap);
 	}

+
 	if (oap->line_count == 1)	// delete characters within one line
 	{
 	    if (u_save_cursor() == FAIL)	// save line for undo
