patch:
diff --git a/src/search.c b/src/search.c
index 01c143f69..8a4bd67e5 100644
--- a/src/search.c
+++ b/src/search.c
@@ -1549,15 +1549,18 @@ do_search(
 			    vim_free(msgbuf);
 			    msgbuf = r;
 			    // move reversed text to beginning of buffer
-			    while (*r != NUL && *r == ' ')
-				r++;
-			    pat_len = msgbuf + msgbuflen - r;
-			    mch_memmove(msgbuf, r, pat_len);
+                while (*r != NUL && *r == ' ')
+                    r++;
+                pat_len = STRLEN(r);
+                if (pat_len > msgbuflen)
+                    pat_len = msgbuflen;
+                if (pat_len > 0)
+                    mch_memmove(msgbuf, r, pat_len);
 			    // overwrite old text
-			    if ((size_t)(r - msgbuf) >= pat_len)
-				vim_memset(r, ' ', pat_len);
-			    else
-				vim_memset(msgbuf + pat_len, ' ', r - msgbuf);
+                if ((size_t)(r - msgbuf) >= pat_len)
+                    vim_memset(r, ' ', pat_len);
+                else if (pat_len > 0)
+                    vim_memset(msgbuf + pat_len, ' ', (size_t)(r - msgbuf));
 			}
 		    }
 #endif
