patch:
diff --git a/src/search.c b/src/search.c
index 01c143f69..530415711 100644
--- a/src/search.c
+++ b/src/search.c
@@ -1548,16 +1548,18 @@ do_search(
 			{
 			    vim_free(msgbuf);
 			    msgbuf = r;
-			    // move reversed text to beginning of buffer
-			    while (*r != NUL && *r == ' ')
-				r++;
-			    pat_len = msgbuf + msgbuflen - r;
-			    mch_memmove(msgbuf, r, pat_len);
-			    // overwrite old text
-			    if ((size_t)(r - msgbuf) >= pat_len)
-				vim_memset(r, ' ', pat_len);
-			    else
-				vim_memset(msgbuf + pat_len, ' ', r - msgbuf);
+                // move reversed text to beginning of buffer, safely
+                char_u *buf_start = msgbuf;
+                size_t buf_len = STRLEN(msgbuf);
+                while (*r != NUL && *r == ' ')
+                    r++;
+                pat_len = STRLEN(r);
+                if (pat_len > buf_len)
+                    pat_len = buf_len;
+                mch_memmove(buf_start, r, pat_len);
+                // overwrite old text
+                if (buf_len > pat_len)
+                    vim_memset(buf_start + pat_len, ' ', buf_len - pat_len);
 			}
 		    }
 #endif
