patch:
diff --git a/src/ex_cmds.c b/src/ex_cmds.c
index e1e6c4ee7..2a61756e9 100644
--- a/src/ex_cmds.c
+++ b/src/ex_cmds.c
@@ -41,7 +41,15 @@ do_ascii(exarg_T *eap UNUSED)
     int		len;

     if (enc_utf8)
-	c = utfc_ptr2char(ml_get_cursor(), cc);
+    {
+	char_u *p = ml_get_cursor();
+	colnr_T remaining = ml_get_cursor_len();
+	
+	if (remaining > 0 && utf_ptr2len_len(p, remaining) <= remaining)
+	    c = utfc_ptr2char(p, cc);
+	else
+	    c = remaining > 0 ? *p : NUL;
+    }
     else
 	c = gchar_cursor();
     if (c == NUL)
