patch:
diff --git a/src/popupmenu.c b/src/popupmenu.c
index 71bb49984..a0f9d2f74 100644
--- a/src/popupmenu.c
+++ b/src/popupmenu.c
@@ -841,7 +841,8 @@ pum_redraw(void)
 				    if (used_cells + char_cells > ellipsis_width)
 					break;
 				    used_cells += char_cells;
-				    MB_PTR_BACK(st, st_end);
+				    if (st_end > st)
+					MB_PTR_BACK(st, st_end);
 				    last_char = st_end;
 				}

@@ -849,7 +850,8 @@ pum_redraw(void)
 				{
 				    if (used_cells < ellipsis_width)
 				    {
-					MB_PTR_BACK(st, st_end);
+					if (st_end > st)
+					    MB_PTR_BACK(st, st_end);
 					last_char = st_end;
 					over_cell = ellipsis_width - used_cells;
 				    }
