patch:
diff --git a/src/popupmenu.c b/src/popupmenu.c
index 71bb49984..7b042234b 100644
--- a/src/popupmenu.c
+++ b/src/popupmenu.c
@@ -841,16 +841,24 @@ pum_redraw(void)
 				    if (used_cells + char_cells > ellipsis_width)
 					break;
 				    used_cells += char_cells;
-				    MB_PTR_BACK(st, st_end);
+			    if (st_end > st)
+			    {
+				MB_PTR_BACK(st, st_end);
+				if (st_end >= st)
 				    last_char = st_end;
+			    }
 				}

 				if (last_char != NULL)
 				{
 				    if (used_cells < ellipsis_width)
 				    {
-					MB_PTR_BACK(st, st_end);
+				if (st_end > st)
+				{
+				    MB_PTR_BACK(st, st_end);
+				    if (st_end >= st)
 					last_char = st_end;
+				}
 					over_cell = ellipsis_width - used_cells;
 				    }
 				    kept_len = last_char - st;
