patch:
diff --git a/src/tuple.c b/src/tuple.c
index eff4bdccc..25de9abc2 100644
--- a/src/tuple.c
+++ b/src/tuple.c
@@ -592,8 +592,13 @@ eval_tuple(char_u **arg, typval_T *rettv, evalarg_T *evalarg, int do_error)
 	if (do_error)
 	    semsg(_(e_missing_end_of_tuple_rsp_str), *arg);
 failret:
-	if (evaluate)
-	    tuple_free(tuple);
+    if (evaluate)
+    {
+        tuple_free(tuple);
+        // Prevent double-free: tuple_free will clear and free its items,
+        // including any typval_T passed in as 'tv', so mark as unknown.
+        rettv->v_type = VAR_UNKNOWN;
+    }
 	return FAIL;
     }
