patch:
diff --git a/src/eval.c b/src/eval.c
index bbfe566fa..521ad0649 100644
--- a/src/eval.c
+++ b/src/eval.c
@@ -5007,7 +5007,15 @@ eval9_nested_expr(

 	    if (**arg == ',')
 		// tuple
-		ret = eval_tuple(arg, rettv, evalarg, TRUE);
+    {
+        ret = eval_tuple(arg, rettv, evalarg, TRUE);
+        if (ret != OK)
+        {
+            // eval_tuple() frees the tuple and its items, including rettv,
+            // so prevent double free by marking rettv as unknown.
+            rettv->v_type = VAR_UNKNOWN;
+        }
+    }
 	    else if (**arg == ')')
 		++*arg;
 	    else if (ret == OK)
