patch:
diff --git a/xmpsdk/src/XMPUtils.cpp b/xmpsdk/src/XMPUtils.cpp
index 8474b8dd3..3e2be7364 100644
--- a/xmpsdk/src/XMPUtils.cpp
+++ b/xmpsdk/src/XMPUtils.cpp
@@ -1960,7 +1960,14 @@ XMPUtils::SetTimeZone ( XMP_DateTime * xmpTime )
 		if ( now == -1 ) XMP_Throw ( "Failure from ANSI C time function", kXMPErr_ExternalFailure );
 		ansi_localtime ( &now, &tmLocal );
 	} else {
-		tmLocal.tm_year = xmpTime->year - 1900;
+		// Safely compute year - 1900 to avoid signed integer overflow
+		if ( xmpTime->year == std::numeric_limits<XMP_Int32>::min() ) {
+			// Special case for minimum value to avoid overflow
+			XMP_Throw ( "Year value out of range for time zone conversion", kXMPErr_BadParam );
+		} else {
+			XMP_Int32 adjustedYear = xmpTime->year - 1900;
+			tmLocal.tm_year = static_cast<int>(adjustedYear);
+		}
 #if 0
 		// Removed to fix https://github.com/Exiv2/exiv2/issues/1901
 		while ( tmLocal.tm_year < 70 ) tmLocal.tm_year += 4;	// ! Some versions of mktime barf on years before 1970.
