 #endif
 #endif
+
+#include <climits>

 // ... rest of file ...
@@ -1962,7 +1963,17 @@
 	} else {
-		tmLocal.tm_year = xmpTime->year - 1900;
+		// Check for overflow/underflow before subtracting 1900
+		if (xmpTime->year < (std::numeric_limits<int>::min() + 1900) ||
+		    xmpTime->year > (std::numeric_limits<int>::max() + 1900)) {
+			XMP_Throw("Year value out of range in SetTimeZone", kXMPErr_BadParam);
+		}
+		tmLocal.tm_year = static_cast<int>(xmpTime->year - 1900);
