+#include <limits.h>

 #include <time.h>
 #include <string.h>
@@ -1960,7 +1961,15 @@ XMPUtils::SetTimeZone ( XMP_DateTime * xmpTime )
 		if ( now == -1 ) XMP_Throw ( "Failure from ANSI C time function", kXMPErr_ExternalFailure );
 		ansi_localtime ( &now, &tmLocal );
 	} else {
-		tmLocal.tm_year = xmpTime->year - 1900;
+		/* Prevent signed integer overflow and ensure tm_year fits in int */
