patch:
diff --git a/src/quicktimevideo.cpp b/src/quicktimevideo.cpp
index 1c54b01b9..c58739aea 100644
--- a/src/quicktimevideo.cpp
+++ b/src/quicktimevideo.cpp
@@ -620,6 +620,9 @@ void QuickTimeVideo::tagDecoder(Exiv2::DataBuf& buf, size_t size) {
     keysTagDecoder(size);

   else if (equalsQTimeTag(buf, "url ")) {
+    if (buf.size() < size) {
+      buf.resize(size);
+    }
     io_->readOrThrow(buf.data(), size);
     if (currentStream_ == Video)
       xmpData_["Xmp.video.URL"] = Exiv2::toString(buf.data());
@@ -628,6 +631,9 @@ void QuickTimeVideo::tagDecoder(Exiv2::DataBuf& buf, size_t size) {
   }

   else if (equalsQTimeTag(buf, "urn ")) {
+    if (buf.size() < size) {
+      buf.resize(size);
+    }
     io_->readOrThrow(buf.data(), size);
     if (currentStream_ == Video)
       xmpData_["Xmp.video.URN"] = Exiv2::toString(buf.data());
@@ -636,6 +642,9 @@ void QuickTimeVideo::tagDecoder(Exiv2::DataBuf& buf, size_t size) {
   }

   else if (equalsQTimeTag(buf, "dcom")) {
+    if (buf.size() < size) {
+      buf.resize(size);
+    }
     io_->readOrThrow(buf.data(), size);
     xmpData_["Xmp.video.Compressor"] = Exiv2::toString(buf.data());
   }
