patch:
diff --git a/src/pngimage.cpp b/src/pngimage.cpp
index 4ccec39d8..fe18c05e6 100644
--- a/src/pngimage.cpp
+++ b/src/pngimage.cpp
@@ -467,7 +467,11 @@ namespace Exiv2 {
                             break;
                          }
                     }
-                    profileName_ = std::string(reinterpret_cast<char *>(chunkData.pData_), iccOffset-1);
+                    // Ensure we have a valid profile name (iccOffset > 0 means we found a null terminator)
+                    if (iccOffset > 0) {
+                        profileName_ = std::string(reinterpret_cast<char *>(chunkData.pData_), iccOffset-1);
+                    }
+                    // If no null terminator found, profileName_ remains empty
                     ++iccOffset; // +1 = 'compressed' flag
                     enforce(iccOffset <= chunkLength, Exiv2::kerCorruptedMetadata);
