diff --git a/src/pngimage.cpp b/src/pngimage.cpp
index 4ccec39d8..569b5cc53 100644
--- a/src/pngimage.cpp
+++ b/src/pngimage.cpp
@@ -463,11 +463,17 @@ namespace Exiv2 {
                     // The ICC profile name can vary from 1-79 characters.
                     uint32_t iccOffset = 0;
                     while (iccOffset < 80 && iccOffset < chunkLength) {
-                         if (chunkData.pData_[iccOffset++] ==  0x00) {
+                        if (chunkData.pData_[iccOffset++] == 0x00) {
                             break;
-                         }
+                        }
+                    }
+                    // After the loop, iccOffset points to the byte after the null terminator (or chunkLength if not found)
+                    if (iccOffset == 0 || iccOffset > chunkLength || chunkData.pData_[iccOffset-1] != 0x00) {
+                        throw Exiv2::Error(kerCorruptedMetadata);
                     }
                     profileName_ = std::string(reinterpret_cast<char *>(chunkData.pData_), iccOffset-1);
+                    // Now iccOffset points to the compressed flag
+                    enforce(iccOffset < chunkLength, Exiv2::kerCorruptedMetadata);
                     ++iccOffset; // +1 = 'compressed' flag
                     enforce(iccOffset <= chunkLength, Exiv2::kerCorruptedMetadata);

