patch:
diff --git a/src/pngimage.cpp b/src/pngimage.cpp
index 4ccec39d8..236c9308e 100644
--- a/src/pngimage.cpp
+++ b/src/pngimage.cpp
@@ -467,6 +467,10 @@ namespace Exiv2 {
                             break;
                          }
                     }
+                    // Fix: Prevent heap buffer overflow by checking iccOffset
+                    if (iccOffset == 0 || (iccOffset - 1) > chunkLength) {
+                        throw Exiv2::Error(kerCorruptedMetadata);
+                    }
                     profileName_ = std::string(reinterpret_cast<char *>(chunkData.pData_), iccOffset-1);
                     ++iccOffset; // +1 = 'compressed' flag
                     enforce(iccOffset <= chunkLength, Exiv2::kerCorruptedMetadata);
