@@ -652,6 +652,14 @@ H5D__scatgath_write(const H5D_io_info_t *io_info, const H5D_dset_io_info_t *dset
     assert(dset_info->file_space);
     assert(dset_info->buf.cvp);

+    /* Additional NULL checks to prevent null pointer dereference */
+    if (dset_info->buf.vp == NULL) {
+        HGOTO_ERROR(H5E_DATASET, H5E_ARGS, FAIL, "NULL user buffer pointer");
+    }
+    if (dset_info->layout_io_info.contig_piece_info == NULL) {
+        HGOTO_ERROR(H5E_DATASET, H5E_ARGS, FAIL, "NULL contig_piece_info pointer");
+    }
+
     /* Set buf pointer */
     buf = dset_info->buf.cvp;

