   }

-  bool incRecursionDepth(ESTree::Node *) {
-    return true;
-  }
-
-  void decRecursionDepth() {}
+  bool incRecursionDepth(ESTree::Node *) {
+    return true; // No-op for iterative traversal
+  }
+  void decRecursionDepth() {}

  private:
   Context &context_;
   UniqueString *const identVar_;
   VisitedClass *_currentProcessingClass = nullptr;
   const ResolvedClassMember *_currentClassMember = nullptr;
+
+// Custom iterative traversal to prevent stack overflows
+friend void visitESTreeChildren(ES6ClassesTransformations &v, ESTree::Node *root);
 };
+
+// Custom iterative traversal for ES6ClassesTransformations
+void visitESTreeChildren(ES6ClassesTransformations &v, ESTree::Node *root) {
+  if (!root) return;
+  std::vector<ESTree::Node *> stack;
+  stack.push_back(root);
+  while (!stack.empty()) {
+    ESTree::Node *node = stack.back();
+    stack.pop_back();
+    // Use the macro-generated switch to visit children without recursion.
+    switch (node->getKind()) {
+      default:
+        break;
+#define VISIT(NAME) \
+      case ESTree::NodeKind::NAME: { \
+        auto *typed = llvh::cast<ESTree::NAME##Node>(node); \
+        ESTree::detail::pushChildren(stack, typed); \
+        break; \
+      }
+#define ESTREE_NODE_0_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_1_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_2_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_3_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_4_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_5_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_6_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_7_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_8_ARGS(NAME, ...) VISIT(NAME)
+#define ESTREE_NODE_9_ARGS(NAME, ...) VISIT(NAME)
+#include \"hermes/AST/ESTree.def\"
+#undef VISIT
+    }
+    // Perform any node-specific logic here, if necessary.
+  }
+}
```

