index 5aab5d3de..38382981b 100644
--- a/include/hermes/VM/JSCallableProxy.h
+++ b/include/hermes/VM/JSCallableProxy.h
@@ -38,8 +38,14 @@ class JSCallableProxy : public NativeFunction {
       Handle<JSObject> handler);

   bool isConstructor(Runtime *runtime) {
+    auto *target = slots_.target.get(runtime);
+    // Break infinite recursion: if the target is also a JSCallableProxy, return false.
+    if (auto *proxy = dyn_vmcast<JSCallableProxy>(target)) {
+      // If the target is the same as this, or another proxy, break the cycle.
+      return false;
+    }
     return vm::isConstructor(
-        runtime, vmcast_or_null<Callable>(slots_.target.get(runtime)));
+        runtime, vmcast_or_null<Callable>(target));
   }

